import React from 'react';

const Book = ({ image, name, author }) => {
  return (
    <div className="book">
      <img src={image} alt={"image is not available now"} className="book-image" />
      <div className="book-details">
        <h3>{name}</h3>
        <p >by {author}</p>
      </div>
    </div>
  );
};

export default Book;
