import React, { useState } from 'react';
import books from './Bookdata';
import Book from './Book'; 
import BooksAvailable from './BookAvailable'

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchTerm(query);

    if (query.trim() === '') {
      setSearchResults([]);
      return;
    }

    const filteredBooks = books.filter(book =>
      book.title.toLowerCase().includes(query.toLowerCase()) ||
      book.author.toLowerCase().includes(query.toLowerCase())
    );

    
    setSearchResults(filteredBooks);
  };

  return (
    <>
    <div className="search-container"> {/* Apply class name */}
      <h1 className="title" style={{"margin-top":"0"}}>Book Search App</h1>
      <input
        type="text"
        placeholder="Search for books..."
        value={searchTerm}
        onChange={handleSearch}
        className="search-input"
      />
      {/* Display search results */}
      <div className="search-results">
        {searchResults.length > 0 && <h2>Results are</h2>}
        {searchResults.map(book => (
          <Book
            key={book.id}
            image={book.imageUrl}
            name={book.title}
            author={book.author}
            rating={book.ratings}
            price={book.price}
          />
        ))}
      </div>
    </div>
    {searchResults.length === 0 && <BooksAvailable books={books} />}
    </>
  );
};

export default SearchBar;
